import React, { Component } from 'react';
import axios from 'axios';

let endpoint = "http://localhost:8000";

class Form extends Component {
    constructor(props){
        super(props)
        this.state = {
            input: ''
        }
    }

    isClicked = () => {
        fetch(endpoint+"/user", {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name : "maheen"
            })
        })

    }

    render () {
        return(
            <form>
                <label>
                    Name:
                    <input type="text" name={this.state.input} onChange={this.handleChange} />
                </label>
                <input type="submit" onClick={this.isClicked} value="Submit" />
            </form>
        )
    }
}

export default Form;
