import React, { Component } from 'react';

class Form extends Component {
    constructor(props){
        super(props)
        this.state = {
            input: ''
        }
    }

    isClicked = () => {
        axios.post('/user', {
            firstName: this.state.input
          })
          .then(function (response) {
            console.log(response);
          })
          .catch(function (error) {
            console.log(error);
        });
    }

    handleChange = (event) => {
        this.setState({
            input: event.target.value
        })
    }

    render () {
        return(
            <form>
                <label>
                    Name:
                    <input type="text" name={this.state.input} onChange={this.handleChange} />
                </label>
                <input type="submit" onClick={this.isClicked} value="Submit" />
            </form>
        )
    }
}

export default Form;
